return {
  url: `https://quickchart.io/chart?w=800&h=400&bkg=%23fff&v=2.9.4&c={
    type:'bar',
    data:{
      labels:[${$input.first().json.Month.map(m => `'${m}'`).join(",")}],
      datasets:[
        {label:'Total Amount Spent',data:[${$input.first().json['Total Spent'].map(m => m).join(",")}]},
        {label:'Average Spend Amount',data:[${$input.first().json['Average Spent Ammount'].map(m => m).join(",")}]},
        {label:'Highest Spend Amount',data:[${$input.first().json['Highest Expense'].map(m => m).join(",")}]},
        {label:'Lowest Spend Amount',data:[${$input.first().json['Lowest Expense'].map(m => m).join(",")}]}
      ]
    },
    options: {
      title: {
        display: true,
        text: 'Monthly Expense Comparison'
      },
      plugins: {        
        datalabels: {
          display: true,
          color: 'black',
          anchor: 'end',
          align: 'top',
          font: { weight: 'bold' }
        }
      }
    },
    plugins:['chartjs-plugin-datalabels']
  }`
};

// Prepare unique months
const months = [...new Set($input.first().json.Months)];

// Prepare unique payment methods
const paymentMethods = [...new Set($input.first().json['Payment Method'])];

// Build datasets
const datasets = paymentMethods.map(method => {
    // For each month, find the transaction count for this payment method
    const data = months.map(month => {
        const idx = $input.first().json.Months.findIndex((m, i) => m === month && $input.first().json['Payment Method'][i] === method);
        return idx !== -1 ? Number($input.first().json['Transaction Count'][idx]) : 0;
    });
    return `{label:'${method}',data:[${data.join(",")}]}`;
});

// Find maximum value to calculate suggestedMax
const allCounts = $input.first().json['Transaction Count'].map(Number);
const maxCount = Math.max(...allCounts);
const suggestedMax = maxCount + Math.ceil(maxCount * 0.4); // add 10% buffer

return {
    url: `https://quickchart.io/chart?w=800&h=400&bkg=%23fff&v=2.9.4&c={
        type:'bar',
        data:{
            labels:[${months.map(m => `'${m}'`).join(",")}],
            datasets:[${datasets.join(",")}]
        },
        options:{
            title:{
                display:true,
                text:'Monthly Transaction Counts by Payment Method'
            },
            scales:{
                y:{
                    beginAtZero:true,
                    suggestedMax:${suggestedMax}
                }
            },
            plugins:{
                datalabels:{
                    display:true,
                    color:'black',
                    anchor:'end',
                    align:'top',
                    font:{weight:'bold'}
                }
            }
        },
        plugins:['chartjs-plugin-datalabels']
    }`
};

const now = new Date();
const monthNames = [
  "January", "February", "March", "April", "May", "June",
  "July", "August", "September", "October", "November", "December"
];
const currentMonth = monthNames[now.getMonth()];
const currentYear = now.getFullYear();
const title = `Expense Statement for ${currentMonth} - ${currentYear}`;

let rawList = $input.first().json.list;
let dataSource = rawList.map(e => ({
  transaction_date: e.transaction_date,
  amount: `₹ ${parseFloat(e.amount)}`,
  category: e.category,
  paymentMethod: e.payment_method,
  merchant: e.merchant
}));

// Add Grand Total row
const totalAmount = rawList.reduce((sum, e) => sum + parseFloat(e.amount), 0);
dataSource.push({
  transaction_date: "Grand Total",
  amount: `₹ ${totalAmount.toFixed(2)}`,
  category: "",
  paymentMethod: "",
  merchant: ""
});
return { 
  data: {
    "data": {
      "title": title,
      "columns": [
        { width: 200, title: "Transaction Date", dataIndex: "transaction_date" },
        { width: 200, title: "Category", dataIndex: "category" },
        { width: 200, title: "Payment Method", dataIndex: "paymentMethod" },
        { width: 200, title: "Merchant", dataIndex: "merchant" },
        { width: 100, title: "Amount", dataIndex: "amount" }
      ],
      "dataSource": dataSource
    }
  }
};
