options.customCss:
:root {
  /* Colors - transparent base for glassmorphism */
  --chat--color-primary: rgba(231, 66, 102, 0.6);
  --chat--color-primary-shade-50: rgba(219, 64, 97, 0.5);
  --chat--color-primary-shade-100: rgba(207, 60, 92, 0.5);
  --chat--color-secondary: rgba(32, 182, 158, 0.6);
  --chat--color-secondary-shade-50: rgba(28, 160, 138, 0.5);
  --chat--color-white: rgba(255, 255, 255, 0.8);
  --chat--color-light: rgba(255, 255, 255, 0.3);
  --chat--color-light-shade-50: rgba(255, 255, 255, 0.15);
  --chat--color-light-shade-100: rgba(255, 255, 255, 0.1);
  --chat--color-medium: rgba(210, 212, 217, 0.3);
  --chat--color-dark: rgba(16, 19, 48, 0.7);
  --chat--color-disabled: rgba(119, 121, 128, 0.4);
  --chat--color-typing: rgba(64, 64, 64, 0.6);

  /* Layout & Fonts */
  --chat--spacing: 1rem;
  --chat--border-radius: 1rem;
  --chat--transition-duration: 0.3s;
  --chat--font-family: (
    -apple-system,
    BlinkMacSystemFont,
    'Segoe UI',
    Roboto,
    'Helvetica Neue',
    sans-serif
  );

  /* Chat Window with Glassmorphism */
  --chat--window--width: 400px;
  --chat--window--height: 600px;
  --chat--window--bottom: var(--chat--spacing);
  --chat--window--right: var(--chat--spacing);
  --chat--window--z-index: 9999;
  --chat--window--border: 1px solid rgba(255, 255, 255, 0.2);
  --chat--window--border-radius: var(--chat--border-radius);
  --chat--window--margin-bottom: var(--chat--spacing);

  /* Header */
  --chat--header-height: auto;
  --chat--header--padding: var(--chat--spacing);
  --chat--header--background: rgba(255, 255, 255, 0.1);
  --chat--header--color: #ffffff;
  --chat--heading--font-size: 1.5em;
  --chat--subtitle--font-size: 1em;

  /* Messages */
  --chat--message--font-size: 1rem;
  --chat--message--padding: 0.75rem;
  --chat--message--border-radius: 1rem;
  --chat--message-line-height: 1.5;
  --chat--message--margin-bottom: 0.75rem;
  --chat--message--bot--background: rgba(255, 255, 255, 0.25);
  --chat--message--bot--color: #101330;
  --chat--message--user--background: rgba(32, 182, 158, 0.6);
  --chat--message--user--color: #fff;
  --chat--messages-list--padding: var(--chat--spacing);

  /* Toggle */
  --chat--toggle--size: 64px;
  --chat--toggle--border-radius: 50%;
  --chat--toggle--background: rgba(231, 66, 102, 0.4);
  --chat--toggle--hover--background: rgba(231, 66, 102, 0.6);
  --chat--toggle--active--background: rgba(231, 66, 102, 0.8);
  --chat--toggle--color: #fff;

  /* Input Area */
  --chat--textarea--height: 50px;
  --chat--input--font-size: 1rem;
  --chat--input--padding: 0.8rem;
  --chat--input--background: rgba(255, 255, 255, 0.2);
  --chat--input--text-color: #000;
  --chat--input--placeholder--font-size: 1rem;

  /* Buttons */
  --chat--button--color: #fff;
  --chat--button--background: rgba(231, 66, 102, 0.6);
  --chat--button--hover--background: rgba(231, 66, 102, 0.8);
  --chat--button--padding: 0.5rem 1rem;
  --chat--close--button--color-hover: #e74266;

  /* Send & File Buttons */
  --chat--input--send--button--background: rgba(255, 255, 255, 0.2);
  --chat--input--send--button--color: #101330;
  --chat--input--file--button--background: rgba(255, 255, 255, 0.2);
  --chat--input--file--button--color: #20b69e;

  /* Body and Footer */
  --chat--body--background: rgba(255, 255, 255, 0.1);
  --chat--footer--background: rgba(255, 255, 255, 0.15);
  --chat--footer--color: #101330;
}

/* Add Glass Effect Globally */
.chat-window,
.chat-footer,
.chat-message {
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  box-shadow: 0 8px 32px rgba(31, 38, 135, 0.2);
  border: 1px solid rgba(255, 255, 255, 0.18);
}

/* Chat message bubble width */
.chat-message {
  max-width: 60%;
}

/* Smooth transitions for interactive elements */
.chat-toggle-button,
.chat-send-button,
.chat-file-button,
.chat-close-button {
  transition: all 0.3s ease;
}
.chat-header{
  display:none !important;
}
.chat-message-from-bot,.chat-message-from-user{
    width: max-content;
    padding: 10px 15px;
}
