=SELECT title
FROM public.linkedin_post_generation_tasks
WHERE EXTRACT(MONTH FROM scheduled_publish_at) ={{ 
  (() => {
    let d = new Date($('Mid Night Trigger to Create Posts').item.json.timestamp);
    d.setDate(d.getDate() + 10);
    return d;
  })().getMonth()+1
}} AND EXTRACT(YEAR FROM scheduled_publish_at) = {{ $('Mid Night Trigger to Create Posts').item.json.Year }};
