// Access the input query object
const input = query;  // input.query was passed, now assigned to `input`

// Predefined views and their descriptions
const views = [
  {
    view: "vw_ClientPrimaryPersonDetails",
    description: "Provides combined client and primary contact details including personal info, demographics, financials, and relationship data for analysis and reporting across business segments."
  }
];

// Check if the query requests the view list
if (input.want_list === true) {
  return JSON.stringify({ views });
} else {
  return JSON.stringify({
    message: "Set 'want_list' to true to retrieve the view descriptions."
  });
}

// Input received from tool
const input = query; // e.g., { vw_name: "vw_ClientPrimaryPersonDetails" }

// Define metadata for each view
const viewDefinitions = {
  "vw_ClientPrimaryPersonDetails": [
    { column_name: "ClientId", datatype: "INT" },
    { column_name: "ClientNumber", datatype: "INT" },
    { column_name: "CompanyName", datatype: "NVARCHAR" },
    { column_name: "DBAName", datatype: "NVARCHAR" },
