    { column_name: "Employer", datatype: "NVARCHAR" },
    { column_name: "Education", datatype: "NVARCHAR" },
    { column_name: "IsDeceased", datatype: "BIT" },
    { column_name: "Comments", datatype: "NVARCHAR" },
    { column_name: "Occupation", datatype: "NVARCHAR" },
    { column_name: "PersonWebsite", datatype: "NVARCHAR" },
    { column_name: "PersonModifiedDate", datatype: "DATETIME" }
  ],
  "ClientSummaryView": [
    { column_name: "ClientId", datatype: "INT" },
    { column_name: "ClientNumber", datatype: "INT" },
    { column_name: "CompanyName", datatype: "NVARCHAR" },
    { column_name: "DBAName", datatype: "NVARCHAR" },
    { column_name: "ClientType", datatype: "NVARCHAR" },
    { column_name: "BusinessType", datatype: "NVARCHAR" },
    { column_name: "Source", datatype: "NVARCHAR" },
    { column_name: "AccountSource", datatype: "NVARCHAR" },
    { column_name: "IsCommercial", datatype: "BIT" },
    { column_name: "IsPersonal", datatype: "BIT" },
    { column_name: "IsLifeHealth", datatype: "BIT" },
    { column_name: "ClientSince", datatype: "DATETIME" },
    { column_name: "Active", datatype: "BIT" },
    { column_name: "AgencyName", datatype: "NVARCHAR" },
    { column_name: "OfficeDescription", datatype: "NVARCHAR" }
  ]
};

// Validate and return columns for the requested view
const viewName = input.vw_name;

if (viewDefinitions[viewName]) {
  return JSON.stringify({
    view: viewName,
    columns: viewDefinitions[viewName]
  });
} else {
  return JSON.stringify({
    error: `View "${viewName}" not found.`
  });
}

