const fields = {
  FolderId: 'string',
  AgencyId: 'string',
  Name: 'string',
  Type: 'A',
  WebhookURL: 'AITesting',
  TriggerCategoriesName: 'string',
  TriggerCategoryId: 'string',
  TriggerTypeId: 'string',
  TriggerTypesConditionsFilterId: 'string',
  TriggerTypeName: 'string',
  FilterValue: 'string',
  UserId: 'string',
  TouchpointStatus: 'string',
  ClientBirthDate: 'string',

  // Array fields
  IncludeLOBCodeIds: 'array',
  LOBCategoryIds: 'array',
  TriggerTypesConditionIds: 'array',
  ExcludeLOBCodeIds: 'array',
  CarrierIds: 'array',
  AgentIds: 'array',
  CSRIds: 'array',
  ExcludeTagIds: 'array',
  IncludeTagIds: 'array'
};

const input = $input.first().json;

if (input?.state?.folder?.id) {
  fields.FolderId = input.state.folder.id;
}
if (input?.state?.agency?.id) {
  fields.AgencyId = input.state.agency.id;
}
if (input?.state?.name?.name) {
  fields.Name = input.state.name.name;
}
if (input?.state?.category) {
  fields.TriggerCategoriesName = input.state.category.name;
  fields.TriggerCategoryId = input.state.category.id;
}
if (input?.state?.type) {
  fields.TriggerTypeId = input.state.type.id;
  fields.TriggerTypeName = input.state.type.name;
}
if (input?.state?.include_tags?.selected_options?.length) {
  fields.IncludeTagIds = input.state.include_tags.selected_options.map(e => e.id);
}
if (input?.state?.exclude_tags?.selected_options?.length) {
  fields.ExcludeTagIds = input.state.exclude_tags.selected_options.map(e => e.id);
}
if (input?.state?.condition?.selected_options?.length) {
  fields.TriggerTypesConditionIds = input.state.condition.selected_options.map(e => e.id);
}

// âœ… Remove default placeholder values
const cleanedFields = Object.fromEntries(
  Object.entries(fields).filter(([key, value]) => {
    if (value === 'string' || value === 'array') return false; // remove defaults
    if (Array.isArray(value) && value.length === 0) return false; // remove empty arrays
    return true; // keep only meaningful values
  })
);

return { fields: cleanedFields };
